import axios from "axios"
const API=axios.create({baseURL:"http://localhost:9363"})


export const fetchMemory=async ()=> await API.axios.get("/post/panel");
export const fetchUsers=async ()=> await API.axios.get("/user/users");
export const fetchDelete=async (id)=> await API.axios.delete(`/post/panel/${id}`);
export const fetchDetay=async (id)=> await API.axios.get(`/post/detay/${id}`);
export const fetchPost=async (post)=> await API.axios.post("/post/panel",post);
export const fetchUpdate=async (Updatepost,id)=> await API.axios.put(`/post/panel/${id}`,Updatepost);
export const fetchAdmin=async (form)=> await API.axios.put("/user/admin",form);
export const fetchSignin=async (form)=> await axios.post("/user/signin",form);
export const fetchRegister=async (formData)=> await axios.post("http://localhost:9363/user/uye-ol",formData);
