import React from "react";
import axios from "axios";
import { fetchMemory,fetchDelete,fetchPost } from '../../api';
export const yaziListesiGetir = () => (dispatch) => {
  fetchMemory()
    .then((response) => {
      dispatch({ type: "YAZI_LISTESI_GETIR", payload: response.data });

    })
    .catch(() => {

    });
};
export const yazıEkle=(post)=>(dispatch)=>{
  fetchPost(post)
  .then((response)=>{
    dispatch({type:"MEMORY_EKLE",payload:response.data})
  })
  .catch(() => {

  });
};
export const yazıSil=(id)=>(dispatch)=>{
  fetchDelete(id)
  .then((response)=>{
    dispatch({type:"MEMORY_SIL",payload:id})
  })
  .catch(() => {

  });
};
