import React, { useState,useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { fetchUserDuzGetır,fetchUserDuzenle } from '../api';
import { useParams } from 'react-router-dom';
const Register = () => {
    const { email } = useParams();
    const [data, setData] = useState({
      firstName: "",
      lastName: "",
      email: "",
      password: "",

    });
  const user=JSON.parse(localStorage.getItem("user"))
    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await fetchUserDuzenle(email);
          setData(response.data);
          console.log(response.data);
        } catch (error) {
          console.error("Error fetching user data:", error.message);
          // Handle the error, you may want to set an error state or log it.
        }
      };

      fetchData();
    }, [email]);

    const onChange = (e) => {
      setData({ ...data, [e.target.name]: e.target.value });
    };
    const Navigate = useNavigate();
    const onSubmit = async (e) => {
      e.preventDefault();
      try {
        const response = fetchUserDuzGetır(email, data);
        // localStorage.setItem('token', response.data.token);
        Navigate("/kullanicilar");

        console.log("Registration successful:", response.data);
      } catch (error) {
        console.error("Registration failed:", error.message);
      }
    };

    return (
        <div className='body '>
            <nav>
                <img src="./images/logo.webp" alt="logo" style={{ width: "120px", height: "60px" }} />
            </nav>
            <div className="form-wrapper " >
                <h2 >Güncelle</h2>
                <form action="#" onSubmit={onSubmit} className="mx-auto">
                    <div className="form-control">
                        <input
                            className="form-control"
                            type="text"
                            name="firstName"
                            value={data.firstName}
                            onChange={onChange}



                        />
                        <label>Ad</label>
                    </div>
                    <div className='form-control
                '>
                        <input
                            className="form-control"

                            type="text"
                            name="lastName"
                            onChange={onChange}
                            value={data.lastName}
                        />
                        <label>Soyad</label>
                    </div>
                    <div className="form-control">
                        <input type="email" className='form-control' name="email" onChange={onChange}    value={data.email}/>
                        <label>Email</label>
                    </div>

                    <div className="form-control">
                        <input type="password" className='form-control' name="password" value={data.password} onChange={onChange}  />
                        <label>Parola</label>
                    </div>


                    <button type="submit">Kayıt Ol</button>
                    <Link to="/giris-yap"> Giris Yap</Link>
                    <div className="form-help">
                        <div className="remember-me ">
                        </div>
                        <Link to="">Sana nasıl yardımcı olabilirim ?</Link>
                    </div>
                </form>

            </div >
        </div >
    )
}

export default Register
