const INITIAL_STATE={
    yaziListesi:[]
};
export default (state=INITIAL_STATE,action)=>{
    switch (action.type) {
        case 'YAZI_LISTESİ_GETIR':
        return {...state,yaziListesi:action.payload}

            break;

        default:
            return state;
    }
}
